import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super();
    this.state= {
      trestbps_value : '',
      chol_value: '',
      thalach_value: '',
      oldpeak_value: ''
    }

  }

  slidervalue_trestbps = (Event) =>{
    this.setState({trestbps_value: Event.target.value})
  }

  slidervalue_chol = (Event) => {
    this.setState({chol_value: Event.target.value})
  }

  slidervalue_thalach = (Event) => {
    this.setState({thalach_value: Event.target.value})
  }

  slidervalue_oldpeak = (Event) => {
    this.setState({oldpeak_value: Event.target.value})
  }
  // componentDidMount() {
  //   fetch('http://127.0.0.1:1812/')
  //       .then(Response => Response.json())
  //       .then(console.log)
  // }


  render() {
    return (
      <div className="App">
        <div>
          <input placeholder="ENTER YOU AGE"></input><br />
        </div>
        <div>
          <label htmlFor="male" >Male</label>
          <input  type="radio" name="gender" value="male"></input>

          <label htmlFor="female">Female</label>
          <input  type="radio" name="gender" value="female"></input><br />
        </div>
        <div>
          <label>
            <b>Chest Pain Type:</b><br />
                    <span />0: Typical angina: chest pain related decrease blood supply to the heart<br />
                    <span />1: Atypical angina: chest pain not related to heart<br />
                    <span />2: Non-anginal pain: typically esophageal spasms (non heart related)<br />
                    <span />3: Asymptomatic: chest pain not showing signs of disease <br />
          </label>
          <input  type="number" min="0" max="4" step="1" ></input><br />
        </div>
        <div>
          <label><b>trestbps:</b></label><br />
          <input value={this.state.trestbps_value} ></input>
          <input  type="range" min="94" max="200" step="1" onChange={this.slidervalue_trestbps}></input><br />
        </div>
        <div>
          <label><b>cholestrol:</b></label><br />
          <input value={this.state.chol_value}></input>
          <input type="range" min="126" max="564" step="1" onChange={this.slidervalue_chol}></input><br />
        </div>
        <div>
          <label><b>fasting blood sugar:</b></label><br />
          <span /><label htmlFor="fbs_no" >Yes</label>
          <input  type="radio" name="fbs" value="fbs_no"></input>

          <label htmlFor="fbs_yes">No</label>
          <input  type="radio" name="fbs" value="fbs_yes"></input><br />
        </div>
        <div>
          <label><b>restecg:</b></label><br />
          <input  type="number" min="0" max="2" step="1"></input><br />
        </div>
        <div>
          <label><b>thalach</b></label><br />
          <input value={this.state.thalach_value}></input>
          <input type="range" min="71" max="202" step="1" onChange={this.slidervalue_thalach}></input><br />
        </div>                    
        <div>
          <label><b>exang:</b></label><br />
          <span /><label htmlFor="exang_no" >Yes</label>
          <input  type="radio" name="exang" value="exang_no"></input>

          <label htmlFor="exang_yes">No</label>
          <input  type="radio" name="exang" value="exang_yes"></input><br />
        </div>
        <div>
          <label><b>Oldpeak:</b></label><br />
          <input value={this.state.oldpeak_value}></input>
          <input type="range" min="0" max="6.2" step="0.2" onChange={this.slidervalue_oldpeak}></input><br />
        </div>
      </div>
    );
  }
}

export default App;
